package net.dreamsynth.app;

import net.dreamsynth.list.LinkedList;

import static net.dreamsynth.utilities.StringUtils.join;
import static net.dreamsynth.utilities.StringUtils.split;
import static net.dreamsynth.app.MessageUtils.getMessage;

import org.apache.commons.text.WordUtils;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
